import React from 'react';

const Education_box= props =>(
    <div className="col-lg-12" data-aos="fade-down">
        <div className="container">
        <small className="float-right">{props.education.startDate} to {props.education.endDate}</small>


        <h2 className="bold">{props.education.schoolName}</h2>

        <h3 >{props.education.title}</h3>
        <h3 className="lead ">{props.education.program}</h3>
</div>
</div>

);

class Education extends React.Component{
    constructor(props){
    super(props)
        this.state={

              educations:[{title:'UnderGraduate',program:'Bachelor of Technology in Information and Communication Technology',schoolName:'Ahmedabad University',startDate:'July,2018',endDate:''},
              ]
        }
    }

    education_list(){
        return this.state.educations.map(current =>{
            return <Education_box education={current}  />;
        })
    }
    render(){
        return(
            <div className="container-fluid education_bg">
                <hr />
                <h1 className="text-center"> Education</h1>
                <div className="row">
                {this.education_list() }
                </div>
                <br />
            </div>
        );
    }
}

export default Education;
